import { mdiGestureTap } from '@mdi/js';
import React, { Component } from "react";
import Card from "./components/Card.js";
import LoaderGif from "./loader.gif";
import Icon from '@mdi/react';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false
    }
  }

  componentDidMount() {
    fetch('https://api.nasa.gov/planetary/apod?api_key=ve02NHvuTanZEQrYK1L5vDCQhp9GCuHpeao68xnB&count=10')
      .then(
        res => res.json()
      ).then(
        json => {
          this.setState({
            isLoaded: true,
            items: json
          });
        }
      );
  }

  render() {
    var { isLoaded, items } = this.state;
    items = shuffle(items);
    const l = Math.ceil(items.length / 2);
    const leftItems = items.splice(0, l);

    if (!isLoaded) {
      return (
        <LoaderState />
      );
    }

    return (
      <div className="App">
        <header className="navbar is-black block">
          <p className="title is-1 has-text-white"> Spacestagram </p>
        </header>

        <div className="container">
          <div className="columns">
            <div className="column is-one-third">
              <LeftHandMenu />
            </div>

            <div className="column is-one third">
              {leftItems.map(item => (
                <Card
                  id={item.hdurl}
                  url={item.url}
                  title={item.title}
                  copyright={item.copyright}
                  date={item.date}
                  explanation={item.explanation.substring(0, item.explanation.indexOf('.') + 1)}
                />
              ))
              }

            </div>

            <div className="column is-one-third">
              {items.map(item => (
                <Card
                  id={item.hdurl}
                  url={item.url}
                  title={item.title}
                  copyright={item.copyright}
                  date={item.date}
                  explanation={item.explanation.substring(0, item.explanation.indexOf('.') + 1)}
                />
              ))
              }
            </div>

          </div>
        </div>
      </div>
    );
  }
}

function LoaderState() {
  return (
    <div className="App">
      <header className="navbar is-black block">
        <p className="title is-1 has-text-white"> Spacestagram </p>
      </header>
      <div className="columns">
        <div className="column is-one-third">
        </div>
        <div className="column is-one-third">
          <figure className="image is-square">
            <img src={LoaderGif} alt="" />
          </figure>
          <p className="title is-4"> Loading ... </p>
        </div>
        <div className="column is-one-third">
        </div>
      </div>
    </div>
  );
}

function shuffle(array) {
  var currentIndex = array.length, temporaryValue, randomIndex;
  while (0 !== currentIndex) {

    // Pick a remaining element...
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex -= 1;

    // And swap it with the current element.
    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }
  return array;
}

const LeftHandMenu = (props) => {
  return (
      <article class="panel is-success">
          <p class="panel-heading">
              You liked the following pictures. Click to check them out! &nbsp;
              <Icon path={mdiGestureTap} size={1} /> 
          </p>
          <a class="panel-block" onClick={() => props.showModal('UndergraduateSD')}>
              Many Moons
          </a>
          {/* <a class="panel-block" onClick={() => props.showModal('UndergraduateIS')}>
              Undergraduate Independent Study
          </a>
          <a class="panel-block" onClick={() => props.showModal('UndergraduateRE')}>
              Undergraduate Research Experience
          </a>
          <a class="panel-block" onClick={() => props.showModal('PhdMastersIS')}>
              PhD and Masters Independent Studies
          </a>
          <a class="panel-block" onClick={() => props.showModal('PhdStudentRS')}>
              PhD Student Research Supervision
          </a>
          <a class="panel-block" onClick={() => props.showModal('MastersStudentRS')}>
              Masters Student Research Supervision
          </a>
          <a class="panel-block" onClick={() => props.showModal('PostdoctoralRS')}>
              Postdoctoral Research Supervision
          </a> */}
      </article>
  );
}

export default App;
