import React, { Component } from "react";
import Card from "./components/Card.js";
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false
    }
  }

  componentDidMount() {
    fetch('https://api.nasa.gov/planetary/apod?api_key=ve02NHvuTanZEQrYK1L5vDCQhp9GCuHpeao68xnB&count=100')
      .then(
        res => res.json()
      ).then(
        json => {
          this.setState({
            isLoaded: true,
            items: json
          });
        }
      );
  }

  render() {
    var { isLoaded, items } = this.state;
    items = shuffle(items);
    const l = Math.ceil(items.length / 2);
    const leftItems = items.splice(0, l);

    if (!isLoaded) {
      return (
        <Loader />
      );
    }

    return (
      <div className="App">
        <header className="navbar is-black block">
          <p className="title is-1 has-text-white"> Spacestagram </p>
        </header>

        <div className="container">
          <div className="columns">
            <div className="column is-one-third">
            </div>

            <div className="column is-one third">
              {leftItems.map(item => (
                <Card
                  id={item.hdurl}
                  url={item.url}
                  title={item.title}
                  copyright={item.copyright}
                  date={item.date}
                  explanation={item.explanation.substring(0, item.explanation.indexOf('.') + 1)}
                />
              ))
              }

            </div>

            <div className="column is-one-third">
              {items.map(item => (
                <Card
                  id={item.hdurl}
                  url={item.url}
                  title={item.title}
                  copyright={item.copyright}
                  date={item.date}
                  explanation={item.explanation.substring(0, item.explanation.indexOf('.') + 1)}
                />
              ))
              }
            </div>

          </div>
        </div>
      </div>
    );
  }
}

function Loader() {
  return (
    <div className="App">
      <header className="navbar is-black block">
        <p className="title is-1 has-text-white"> Spacestagram </p>
      </header>
      <div className="columns">
        <div className="column is-one-third">
        </div>
        <div className="column is-one-third">
          <strong className="title is-4 block"> Look at the stars <br/>
            Look how they shine for you <br/>
            And everything you do <br/>
            Yeah, they were all yellow <br/>
            I came along <br/>
            I wrote a song for you <br/>
            And all the things you do <br/>
            And it was called "Yellow" <br/>
            So then I took my turn <br/>
            Oh, what a thing to have done <br/>
            And it was all yellow ... </strong>
          <progress className="progress is-medium is-dark block" max="100"> 45% </progress>
        </div>
        <div className="column is-one-third">
        </div>
      </div>
    </div>
  );
}

function shuffle(array) {
  var currentIndex = array.length, temporaryValue, randomIndex;
  while (0 !== currentIndex) {

    // Pick a remaining element...
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex -= 1;

    // And swap it with the current element.
    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }
  return array;
}

export default App;
