import React, { Component } from "react";
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false
    }
  }

  // componentDidMount() {
  //   fetch('https://api.nasa.gov/planetary/apod?api_key=ve02NHvuTanZEQrYK1L5vDCQhp9GCuHpeao68xnB&count=20')
  //     .then(
  //       res => res.json()
  //     ).then(
  //       json => {
  //         this.setState({
  //           isLoaded: true,
  //           items: json
  //         });
  //       }
  //     );
  // }

  render() {
    const expl = `Here is a rather typical quasar. But since quasars are so 
    unusual it is quite atypical of most familiar objects. Of the two bright 
    objects in the center of this photo, the quasar is on the left. The bright 
    image to quasar's right is a star, the faint object just above the quasar
     is an elliptical galaxy, with an apparently interacting pair of spiral 
     galaxies near the top. Quasars appear as unresolved points of light, as 
     do stars, and hence quasars were thought to be a type of star until the 
     1960s. We now know that the brightest quasars lie far across the visible 
     universe from us, and include the most distant objects known. Quasars may 
     occupy the centers of galaxies and may even be much brighter than their 
     host galaxies. In fact, the centers of many nearby galaxies have 
     similarities to quasars - including the center of our own Milky Way 
     Galaxy. The exact mechanism responsible for a quasar's extreme brightness 
     is unknown, but thought to involve  supermassive black holes. This picture 
     represents a milestone for the six-year-old Hubble Space Telescope as it 
     was picture number 100,000, taken on June 22, 1996.`


    // var { isLoaded, items } = this.state;

    // if (!isLoaded) {
    //   return (
    //     <div className="App">
    //       <p>
    //         Hello world!
    //       </p>
    //     </div>
    //   );
    // }

    return (
      <div className="App">
        <header className="navbar is-black block">
          <p className="title is-1 has-text-white"> Spacestagram </p>
        </header>

        <div className="container">
          <div className="columns">
            <div className="column is-one-third">
              <Card
                url={"https://apod.nasa.gov/apod/image/2109/saturn2004to2015_peach_960.jpg"}
                title={"Rings and Seasons of Saturn"}
                copyright={"Damian Peach"}
                date={"2021-09-19"}
                explanation={expl.substring(0, expl.indexOf('.') + 1)}
              />
            </div>

            <div className="column is-one third">
              <Card
                url={"https://apod.nasa.gov/apod/image/2109/saturn2004to2015_peach_960.jpg"}
                title={"Rings and Seasons of Saturn"}
                copyright={"Damian Peach"}
                date={"2021-09-19"}
                explanation={expl.substring(0, expl.indexOf('.') + 1)}
              />
            </div>

            <div className="column is-one-third">
              <Card
                url={"https://apod.nasa.gov/apod/image/2109/saturn2004to2015_peach_960.jpg"}
                title={"Rings and Seasons of Saturn"}
                copyright={"Damian Peach"}
                date={"2021-09-19"}
                explanation={expl.substring(0, expl.indexOf('.') + 1)}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

function Card(props) {
  return (
    <div className="card">
      {/** Card image */}
      <div className="card-image">
        <figure className="image is-square">
          <img src={props.url} alt={props.title} />
        </figure>
      </div>

      {/** Card content */}
      <div className="card-content">
        <div className="media">
          <div className="media-content">
            <p className="title is-4">{props.title}</p>
            <p className="subtitle is-6"> © {props.copyright}</p>
          </div>
        </div>
        <div className="content">
          {props.explanation}
        </div>
        <span className="tag">{props.date}</span>
      </div>

      {/** Card footer */}
      <footer className="card-footer">
      <a class="card-footer-item"> Like </a>
      </footer>

    </div>
  );
}

export default App;
