import React, { Component } from "react";
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false
    }
  }

  componentDidMount() {
    fetch('https://api.nasa.gov/planetary/apod?api_key=ve02NHvuTanZEQrYK1L5vDCQhp9GCuHpeao68xnB')
    .then(
      res => res.json()
    ).then(
      json => {
        this.setState({
          isLoaded: true,
          items: json
        });
      }
    );
  }

  render () {
    var {isLoaded, items} = this.state;

    if (!isLoaded) {
      return <div> Loading... </div>
    }

    return (
      <div className="App">
        <p>
          Hello world!
        </p>
      </div>
    );
  }
}

export default App;
