import React, { Component } from "react";
import Card from "./components/Card.js";
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false
    }
  }

  componentDidMount() {
    fetch('https://api.nasa.gov/planetary/apod?api_key=ve02NHvuTanZEQrYK1L5vDCQhp9GCuHpeao68xnB&count=20')
      .then(
        res => res.json()
      ).then(
        json => {
          this.setState({
            isLoaded: true,
            items: json
          });
        }
      );
  }

  render() {
    var { isLoaded, items } = this.state;

    if (!isLoaded) {
      return (
        <Loader />
      );
    }

    return (
      <div className="App">
        <header className="navbar is-black block">
          <p className="title is-1 has-text-white"> Spacestagram </p>
        </header>

        <div className="container">
          <div className="columns">
            <div className="column is-one-third">
            </div>

            <div className="column is-one third">
              {items.map(item => (
                <Card
                  id={item.hdurl}
                  url={item.url}
                  title={item.title}
                  copyright={item.copyright}
                  date={item.date}
                  explanation={item.explanation.substring(0, item.explanation.indexOf('.') + 1)}
                />
              ))
              }

            </div>

            <div className="column is-one-third">
            </div>
          </div>
        </div>
      </div>
    );
  }
}

function Loader() {
  return (
    <div className="App">
      <div className="cloumns">
        <progress className="progress is-medium is-dark" max="100"> 45% </progress>
      </div>
    </div>
  );
}

export default App;
