import React, { Component } from "react";
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false
    }
  }

  // componentDidMount() {
  //   fetch('https://api.nasa.gov/planetary/apod?api_key=ve02NHvuTanZEQrYK1L5vDCQhp9GCuHpeao68xnB&count=20')
  //     .then(
  //       res => res.json()
  //     ).then(
  //       json => {
  //         this.setState({
  //           isLoaded: true,
  //           items: json
  //         });
  //       }
  //     );
  // }

  render() {
    // var { isLoaded, items } = this.state;

    // if (!isLoaded) {
    //   return (
    //     <div className="App">
    //       <p>
    //         Hello world!
    //       </p>
    //     </div>
    //   );
    // }

    return (
      <div className="App">
        <header className="navbar is-black block">
          <p className="title is-1 has-text-white"> Spacestagram </p>
        </header>

        <div className="container">
          <div className="columns">
            <div className="column is-one-third">
              <Card
                url={"https://apod.nasa.gov/apod/image/2109/saturn2004to2015_peach_960.jpg"}
                title={"Rings and Seasons of Saturn"}
                copyright={"Damian Peach"}
              />
            </div>

            <div className="column is-one third">
              <Card
                url={"https://apod.nasa.gov/apod/image/2109/saturn2004to2015_peach_960.jpg"}
                title={"Rings and Seasons of Saturn"}
                copyright={"Damian Peach"}
              />
            </div>

            <div className="column is-one-third">
              <Card
                url={"https://apod.nasa.gov/apod/image/2109/saturn2004to2015_peach_960.jpg"}
                title={"Rings and Seasons of Saturn"}
                copyright={"Damian Peach"}
                date={"2021-09-19"}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

function Card(props) {
  return (
    <div className="card">
      <div className="card-image">
        <figure className="image is-square">
          <img src={props.url} alt={props.title} />
        </figure>
      </div>
      <div className="card-content">
        <div className="media">
          <div className="media-content">
            <p className="title is-4">{props.title}</p>
            <p className="subtitle is-6"> © {props.copyright}</p>
          </div>
        </div>
        <span className="tag">{props.date}</span>
      </div>
    </div>
  );
}

export default App;
